#!/usr/bin/env python3 """ Run Image Analysis for Multimodal Fake News Detection This script executes the comprehensive image analysis pipeline to analyze image quality, metadata, and visual authenticity patterns from the processed Fakeddit dataset. Requirements addressed: 2.3, 2.4 (Image Analysis and Visual Authenticity Patterns) """ import sys import os from pathlib import Path # Add the project root to the Python path project_root = Path(__file__).parent.parent.parent sys.path.insert(0, str(project_root)) from src.data.image_analysis import ImageAnalyzer def main(): """Main function to run image analysis.""" print("Multimodal Fake News Detection - Image Analysis Pipeline") print("=" * 70) try: print("Initializing Image Analyzer...") analyzer = ImageAnalyzer() print("Checking library availability...") if not analyzer.libraries_available['PIL']: print("Warning: PIL/Pillow not available - some image processing features will be limited") if not analyzer.libraries_available['CV2']: print("Warning: OpenCV not available - some advanced image features will be limited") if not analyzer.libraries_available['SCIPY']: print("Warning: SciPy not available - some statistical analyses will be limited") print("Starting complete image analysis pipeline...") print("Analyzing ALL available images (9,837 images)...") success = analyzer.run_complete_analysis(sample_size=None) if success: print("Image analysis completed successfully!") print("=" * 70) print("ANALYSIS SUMMARY") print("=" * 70) summary = analyzer.generate_summary_report() print(summary) else: print("Image analysis failed. Check error messages above.") return 1 except KeyboardInterrupt: print("Analysis interrupted by user") return 1 except Exception as e: print(f"Unexpected error: {e}") import traceback traceback.print_exc() return 1 return 0 if __name__ == "__main__": exit_code = main() sys.exit(exit_code)