#!/usr/bin/env python3 """ Run Text Analysis Pipeline This script executes the comprehensive text analysis for the multimodal fake news detection project. It implements task 6.1 and 6.2 from the implementation plan. Requirements addressed: 2.1, 2.2 (Linguistic Pattern Mining and Authenticity Detection) """ import sys import os from pathlib import Path # Add src directory to path sys.path.append(str(Path(__file__).parent.parent)) from data.text_analysis import TextAnalyzer def main(): """Execute the text analysis pipeline.""" print("=" * 60) print("MULTIMODAL FAKE NEWS DETECTION - TEXT ANALYSIS") print("=" * 60) print() try: print("Initializing Text Analyzer...") analyzer = TextAnalyzer(data_dir="processed_data/text_data") print("Loading processed text data...") if not analyzer.load_processed_data(): print("Error: Failed to load processed data") return False print("Extracting linguistic features (Task 6.1)...") print(" - Analyzing text length, readability, sentiment patterns") print(" - Extracting category-specific linguistic characteristics") print(" - Calculating statistical differences in text patterns") linguistic_features = analyzer.extract_linguistic_features() if linguistic_features: print("Linguistic feature extraction completed") else: print("Error: Linguistic feature extraction failed") return False print("Detecting authenticity patterns (Task 6.2)...") print(" - Identifying distinguishing textual features") print(" - Generating linguistic authenticity signatures") authenticity_patterns = analyzer.detect_authenticity_patterns() if authenticity_patterns: print("Authenticity pattern detection completed") else: print("Error: Authenticity pattern detection failed") return False print("Creating text analysis visualizations...") if analyzer.create_text_visualizations(): print("Visualizations created successfully") else: print("Warning: Some visualizations may have failed") print("Saving analysis results...") if analyzer.save_analysis_results(): print("Results saved successfully") else: print("Error: Failed to save results") return False print("\n" + "=" * 60) print("TEXT ANALYSIS COMPLETED SUCCESSFULLY") print("=" * 60) print() print("Results Location:") print(f" - Analysis Results: {analyzer.output_dir}/") print(f" - Visualizations: {analyzer.viz_dir}/") print() print("Generated Files:") print(" - text_analysis_results.json (comprehensive results)") print(" - TEXT_ANALYSIS_REPORT.md (summary report)") print(" - text_basic_statistics.png") print(" - text_readability_metrics.png") print(" - text_sentiment_analysis.png") print(" - text_keyword_analysis.png") print(" - text_wordclouds.png") print() print("Tasks Completed:") print(" - Task 6.1: Linguistic feature extraction from processed text data") print(" - Task 6.2: Authenticity pattern detection") print() return True except Exception as e: print(f"Error during text analysis: {e}") import traceback traceback.print_exc() return False if __name__ == "__main__": success = main() sys.exit(0 if success else 1)